mapper.update:
UPDATE TBL_RECRUITMENT_FILE
SET UUID = #{uuid}, RECRUITENT_NUMBER = #{recruitmentNumber}, UPLOAD_PATH = #{uploadPath}, FILE_NAME = #{fileName}
WHERE RECRUITENT_NUMBER = #{recruitmentNumber}

mapper.select:
SELECT UUID, RECRUITENT_NUMBER, UPLOAD_PATH, FILE_NAME
FROM TBL_USER_FILE
WHERE RECRUITENT_NUMBER = #{recruitmentNumber}
