mapper.update:
UPDATE TBL_SHOW_REPLY
SET REPLY_CONTENT = #{replyContent}, REPLY_UPDATE_DATE = SYSDATE
WHERE REPLY_NUMBER = #{replyNumber}

mapper.select:

SELECT REPLY_NUMBER, USER_NUMBER, SHOW_NUMBER, REPLY_CONTENT, REPLY_REGISTER_DATE, REPLY_UPDATE_DATE FROM
(
  SELECT ROWNUM RN, REPLY_NUMBER, USER_NUMBER, SHOW_NUMBER, REPLY_CONTENT, REPLY_REGISTER_DATE, REPLY_UPDATE_DATE
  FROM (
  SELECT REPLY_NUMBER, USER_NUMBER, SHOW_NUMBER, REPLY_CONTENT, REPLY_REGISTER_DATE, REPLY_UPDATE_DATE
  FROM TBL_SHOW_REPLY
  ORDER BY REPLY_UPDATE_DATE DESC
  )
  WHERE SHOW_NUMBER = #{showNumber} AND ROWNUM <= #{criteria.pageNum} * #{criteria.amount}
    )
    WHERE RN > (#{criteria.pageNum} - 1 ) * #{criteria.amount}
